import pygame

levels = []
class Levels:
	def add(name):
		def decorator(function):
			levels.append({"Name": name, "Map": function})
			print("Added level", f"'{name}'")
		return decorator
	def get_all():
		return levels
	def get_block(id):
		if id == 1:
			return pygame.transform.scale(pygame.image.load('assets/object/grass_block.png'), (64, 64))
